SELECT ?s ?o ?p ?date ?label ?type WHERE {
	?p dc:creator ?s, ?o ;
   		dcterms:title ?label ;
   	   	dcterms:issued ?date ;
		a ?type .
	?p dcterms:abstract [rdf:value ?abs] .
	filter regex(?abs, "coronavirus")
	filter (?s != ?o)
}
limit 1000

                                                                                                                                                                                                                                 # wdt:P279 = subclass of
# wdt:P31 = instance of
# wd:Q12078 = cancer
# wd:Q3286546 = respiratory disease

select distinct ?p  ?s  ?o ?label ?authorList
from named <http://ns.inria.fr/covid19/graph/entityfishing>
from named <http://ns.inria.fr/covid19/graph/articles>
from named <http://ns.inria.fr/covid19/graph/wikidata-named-entities-full>
where {
    # Look for 2 annotations of the same article with Wikidata URIs ?dis1 and ?dis2
    graph <http://ns.inria.fr/covid19/graph/entityfishing> {
        ?annot1 schema:about ?p; oa:hasBody ?dis1.
        ?annot2 schema:about ?p; oa:hasBody ?dis2.
        filter (?annot1 != ?annot2)
    }
    graph <http://ns.inria.fr/covid19/graph/articles> {
        ?p dct:title ?label.

        {select ?p (group_concat(?name ; separator="--") as ?authorList)
            	where {
			?p dce:creator ?name
		}
         	group by ?p
	}
    }

    graph <http://ns.inria.fr/covid19/graph/wikidata-named-entities-full> {
        { ?dis1 rdfs:label ?s. filter (?dis1 = $term1) } # ?dis1 is wd:Q12078
        UNION
        { ?dis1 wdt:P279 $term1; rdfs:label ?s. }        # ?dis1 is a subclass of wd:Q12078 (at any depth)
        UNION
        { ?dis1 wdt:P31 $term1; rdfs:label ?s. }         # ?dis1 is an instance or a subclass of wd:Q12078

        { ?dis2 rdfs:label ?o. filter (?dis2 = $term2) }
        UNION
        { ?dis2 wdt:P279 $term2; rdfs:label ?o. }
        UNION
        { ?dis2 wdt:P31 $term2; rdfs:label ?o. }
    }

}
limit 1000

                                                                                                                                                                                                    # wdt:P279 = subclass of
# wdt:P31 = instance of
# wd:Q12078 = cancer
# wd:Q1134583 = coronavirus family

select distinct ?p ?s ?o ?label ?pmid ?authorList
from <http://ns.inria.fr/covid19/graph/entityfishing>
from <http://ns.inria.fr/covid19/graph/articles>
from named <http://ns.inria.fr/covid19/graph/wikidata-named-entities-full>
where {
	# Look for 2 annotations of the same article with Wikidata URIs ?dis1 and ?dis2
	?annot1 schema:about ?p; oa:hasBody ?dis1.
	?annot2 schema:about ?p; oa:hasBody ?dis2.
	?p dct:title ?label ;
	bibo:pmid ?pmid .

	graph <http://ns.inria.fr/covid19/graph/wikidata-named-entities-full> {
        	{ ?dis1 rdfs:label ?s. filter (?dis1 = $term1) } # ?dis1 is wd:Q12078
	        UNION
        	{ ?dis1 wdt:P279 wd:Q12078; rdfs:label ?s. }        # ?dis1 is subclass of wd:Q12078
	        UNION
        	{ ?dis1 wdt:P31 wd:Q12078; rdfs:label ?s. }         # ?dis1 is instance or subclass of wd:Q12078

	        { ?dis2 rdfs:label ?o. filter (?dis2 = $term2) }
        	UNION
	        { ?dis2 wdt:P279 wd:Q1134583; rdfs:label ?o. }
    	}
    	{select ?p (group_concat(?name ; separator="--") as ?authorList)
	where {
		?p dce:creator ?name
	}
     	group by ?p
	}
}
limit 1000

SELECT ?s ?o ?p ?date ?label WHERE {
	?annot schema:about ?p ; 
		dcterms:subject ?sub . 
	filter (?sub in ("Medicine", "Biology"))
    	?p dcterms:title ?label ;
	    	dcterms:issued ?date ;
    		dc:creator ?s, ?o .
    	filter contains(?label, "coronavirus")
	filter (?date >= "$beginYear-01-01"^^xsd:date)
	filter (?date <= "$endYear-12-31"^^xsd:date)
	filter (?s < ?o)
}
limit 1000

SELECT ?p (group_concat(distinct ?name ; separator = '--') as ?author) (sample(?d) as ?date) (sample(?t) as ?label)
WHERE {
	select * where {
    		?p dcterms:title ?t ;
			dcterms:issued ?d ;
			dc:creator ?name .
	filter contains(?t, "coronavirus")
	filter (?d >= "$beginYear-01-01"^^xsd:date)
	filter (?d <= "$endYear-12-31"^^xsd:date)
	}
	limit 1000
}
group by ?p

