select ?p (group_concat(distinct ?n ;  separator = '--') as ?author)
(sample(?d) as ?date) (sample(?t) as ?label) (sample(?tt) as ?type)
where {
	select * where {
		?p dcterms:creator ?a ;
			dcterms:type ?tt ;
			dcterms:title ?t .

		#bind (if (?tt in (doc:ConferencePaper, doc:Article, doc:Book, doc:Report), ?tt, doc:Report) as ?atype)

		?p dcterms:issued ?d .
		filter (?d >= "$beginYear-01-01"^^xsd:date)
		filter (?d <= "$endYear-12-31"^^xsd:date)

		?a hsc:structure ?x .
		?x org:unitOf ?s1 .
		?s1 skos:prefLabel "$lab1" . 

		?a hsc:person ?p1 .
		?p1 foaf:name ?n 
 	} limit 10000 
 } 
 group by ?p
 having (count(distinct ?n) > 1)

select ?s ?p ?o ?date ?label ?type  where { 
	?p dcterms:creator ?a1 ;
		dcterms:creator ?a2 ; 
		dcterms:type ?type ;
		dcterms:title ?label ;
		dcterms:issued ?date . 

	filter (?date >= "$beginYear-01-01"^^xsd:date) 
	filter (?date <= "$endYear-12-31"^^xsd:date) 

	?a1 hsc:structure ?x1 .
	?a2 hsc:structure ?x2 . 

	?x1 org:unitOf ?s1 . 
	?x2 org:unitOf ?s2 . 
	?s1 skos:prefLabel  "$lab1" .
	?s2 skos:prefLabel  "$lab2" . 


	?a1 hsc:person ?p1 . 
	?p1 foaf:name ?s .    
	?a2 hsc:person ?p2 . 
	?p2 foaf:name ?o .

	filter (?p1 != ?p2) 
} limit 10000

select ?s ?p ?o ?date ?label ?type  where { 
	?p dcterms:creator ?a1 ;
		dcterms:creator ?a2 ; 
		dcterms:type ?type ;
		dcterms:title ?label ;
		dcterms:issued ?date . 

	filter (?date >= "$beginYear-01-01"^^xsd:date)
	filter (?date <= "$endYear-12-31"^^xsd:date)

	?a1 hsc:structure ?x . 
	?a2 hsc:structure ?y . 

	?x org:unitOf ?sam . 
	?sam skos:prefLabel "$lab1" . 

	?y vs:country-name ?country . 
	filter (?country = db:countryf || ?country = db:countrye ) 

	?a1 hsc:person ?p1 . 
	?p1 foaf:name ?s .   
	?a2 hsc:person ?p2 . 
	?p2 foaf:name ?o . 
	filter (?p1 != ?p2) 
} limit 10000

select distinct ?s ?p ?o ?label ?type ?date ?authorList where {
	{select * where {
	  	?p dcterms:creator ?a ;
			dcterms:title ?label ;
			dcterms:type ?type ;
			dcterms:issued ?date ;
			hsc:topic ?t1, ?t2 .

		filter (?date >= "$beginYear-01-01"^^xsd:date)
		filter (?date <= "$endYear-12-31"^^xsd:date)

		?a hsc:structure ?x .
		?x org:unitOf ?i .
		?i skos:prefLabel "$lab1" .

		?t1 skos:prefLabel ?s .
		?t2 skos:prefLabel ?o .

		filter langMatches(lang(?s), "en")
		filter langMatches(lang(?o), "en")
 		filter(?t1 != ?t2)
		}
	}
	{select ?p (group_concat(?n ; separator="--") as ?authorList) 
	where {
		?p dcterms:creator ?a .
		?a hsc:person ?p1 .
		?p1 foaf:name ?n .
	}
	group by ?p
	}
}

select ?s ?p ?o ?label ?date ?type ?authorList where {
	{select * where {
 		?p dcterms:creator ?a1, ?a2 .

	     	?p dcterms:title ?label ;
     			dcterms:issued ?date ;
	     		dcterms:type ?type .

		filter (?date >= "$beginYear-01-01"^^xsd:date)
		filter (?date <= "$endYear-12-31"^^xsd:date)

	 	?a1 hsc:structure ?x .
		?x org:unitOf ?s1 .
		?s1 skos:prefLabel ?s .

 		?a2 hsc:structure ?y .
		?y org:unitOf ?s2 .
		?s2 skos:prefLabel ?o .

		filter(?s = "$lab1")
		filter(?x != ?y)
		}
	}
	{select ?p (group_concat(?n ; separator="--") as ?authorList) 
		where {
  	      		?p dcterms:creator ?a .
       			?a hsc:person ?p1 .
        		?p1 foaf:name ?n .
    		} 
		group by ?p
  	} 
}

select ?p ?s ?o (sample(?tt) as ?label) (sample(?t) as ?type) (sample(?d) as ?date) (sample(?u) as ?url) where {
	?p a magc:Paper ;
		dcterms:title ?tt ;
		prism:publicationDate ?d ;
		dcterms:creator ?a1, ?a2 ;
		fabio:hasDiscipline ?field ;
		fabio:hasURL ?u ;
		a ?t .
	?field foaf:name "Computer science"^^xsd:string .

	filter (?d >= "$beginYear-01-01"^^xsd:date)
	filter (?d <= "$endYear-12-31"^^xsd:date)

	?a1 org:memberOf ?s1 ; foaf:name ?s .
	?a2 org:memberOf ?s2 ; foaf:name ?o .
	?s1 foaf:name "$lab1"^^xsd:string .
	?s2 foaf:name "$lab1"^^xsd:string .

	filter(?a1 != ?a2)
	filter(?t != magc:Paper)
}
group by ?p ?s ?o
limit 10000

