	{select * where {
		?p dcterms:creator ?a1, ?a2 ;
			dcterms:title ?tt ;
			prism:publicationDate ?d ;
			a ?t ;
			fabio:hasDiscipline ?field ;
			fabio:hasURL ?u .
		?field foaf:name "Computer science"^^xsd:string .

		filter (?d >= "$beginYear-01-01"^^xsd:date)
		filter (?d <= "$endYear-12-31"^^xsd:date)

		?a1 org:memberOf ?x .
		?x foaf:name ?s .
		filter(?s = "$lab1"^^xsd:string)

		?a2 org:memberOf ?y .
		?y foaf:name ?o .

		filter (?x != ?y)
		filter(?t != magc:Paper)

		} limit 10000 
	}
	{select ?p (group_concat(?n ; separator="--") as ?authors) 
	where {
		?p dcterms:creator ?a .
		?a foaf:name ?n .
	}
	group by ?p
	}
} group by ?p ?s ?o

