queries.query:
select * where {
    	?x rdfs:label "Robert Redford"@en .
    	?p dbo:starring ?x, ?a1, ?a2 .
    	?p rdfs:label ?label ;
        	dbp:released ?date ;
		dbp:genre ?type .
    	?a1 rdfs:label ?s filter langMatches(lang(?s), "en")
    	?a2 rdfs:label ?o filter langMatches(lang(?o), "en")
    	filter langMatches(lang(?label), "en")
    	filter (?a1 != ?a2)
}

select distinct ?s (ldv: as ?p) ?o ("fst" as ?style) where { 
	?x ?s ?y . ?y ?o ?z . 
	filter strstarts(str(?s), "http://dbpedia.org/")
	filter strstarts(str(?o), "http://dbpedia.org/")
	filter (?s != ?o) 
} limit 1000

select * where {
	{?s ?p ?o filter(?p = rdfs:subClassOf)} 
	union 
	{?s ?p ?o filter(?p = rdfs:subPropertyOf)} 
	union 
	{?s ?p ?o filter(?p = rdfs:domain)} 
	union 
	{?s ?p ?o filter(?p = rdfs:range)}
}limit 1000

select * where {
	?s ?p ?o filter(?p = rdfs:subPropertyOf)
	bind ("fst" as ?style) 
}limit 100

select * where {
	?s ?p ?o filter(?p = rdfs:subClassOf)
	bind ("snd" as ?style)
}limit 100

select * where {
	{?s ?p ?o filter(?p = rdfs:domain)}
	UNION
	{?s ?p ?o filter(?p = rdfs:range)}

	bind("fst" as ?style1)
	bind("snd" as ?style2)
} limit 100

select distinct ?s ?o (ldv: as ?p) ?style1 ?style2
where {
    {?a ?b ?c. ?a a ?s . filter strstarts(str(?s), "http://dbpedia.org/") filter strstarts(str(?b), "http://dbpedia.org/") bind (?b as ?o)}
    union
    {?a ?b ?c. ?c a ?s . filter strstarts(str(?s), "http://dbpedia.org/") filter strstarts(str(?b), "http://dbpedia.org/") bind (?b as ?o)}
	bind("fst" as ?style2)
	bind("snd" as ?style1)
}
limit 1000

prefix ldv: <http://ldv.fr/path/>
select distinct ?s ?p ?o ("snd" as ?style)
where {
    ?a ?p ?b . ?a a ?s . ?b a ?o filter strstarts(str(?s), "http://dbpedia.org/") filter strstarts(str(?o), "http://dbpedia.org/")
}
limit 1000

PREFIX wsb: <http://ns.inria.fr/wasabi/ontology/>
PREFIX mo: <http://purl.org/ontology/mo/>
PREFIX dcterms: <http://purl.org/dc/terms/>
PREFIX schema: <http://schema.org/>
select distinct ?p ?o ?s ?label where { 
	?p mo:performer ?artist ;
		schema:author ?o ;
		schema:author ?s ;
		dcterms:title ?label .

	?artist foaf:name "Queen" .

	filter(?o != ?s)
}

PREFIX foaf: <http://xmlns.com/foaf/0.1/>
PREFIX schema: <http://schema.org/>
PREFIX dcterms: <http://purl.org/dc/terms/>
prefix wsb:     <http://ns.inria.fr/wasabi/ontology/>
prefix mo:      <http://purl.org/ontology/mo/>

select distinct ?s ?p ?o ?label ?date ?type where { 
    { select * where {
	?p mo:performer ?a1 ; mo:performer ?a2 ; 
		schema:datePublished ?date ;
		dcterms:title ?label .
	?a1 foaf:name ?s .
        ?a2 foaf:name ?o .
        filter ( ?a1 != ?a2 )
        bind ("performer" as ?type)
    } }
    union 
    { select * where {
	?p mo:performer ?a1 ; schema:author ?o ; 
		schema:datePublished ?date ;
		dcterms:title ?label .
	?a1 foaf:name ?s .
        filter ( ?s != ?o )
        bind ("writer" as ?type)
    } }
    union 
    { select * where {
	?p mo:performer ?a1 ; mo:producer ?o ; 
		schema:datePublished ?date ;
		dcterms:title ?label .
	?a1 foaf:name ?s .
        filter( ?s != ?o )
        bind ("producer" as ?type)
    } }
	filter (?s in ( "Michael Jackson", "The Jackson 5") ) 
} limit 1000
